import logo from "./logo.svg";
import "./App.css";
import { useState, useEffect, Children } from "react";
import { createWorker } from "tesseract.js";
import { saveAs } from "file-saver";
import {
  AlignmentType,
  Document,
  HeadingLevel,
  Packer,
  Paragraph,
  TabStopPosition,
  TabStopType,
  TextRun,
} from "docx";
function App() {
  const [ocr, setOcr] = useState("");
  const [imageData, setImageData] = useState(null);
  const worker = createWorker({
    logger: (m) => {
      console.log(m);
    },
  });
  const convertImageToText = async () => {
    if (!imageData) return;
    await worker.load();
    await worker.loadLanguage("eng");
    await worker.initialize("eng");
    const {
      data: { text },
    } = await worker.recognize(imageData);
    setOcr(text);
  };
  useEffect(() => {
    convertImageToText();
  }, [imageData]);

  function handleImageChange(e) {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onloadend = () => {
      const imageDataUri = reader.result;
      console.log({ imageDataUri });
      setImageData(imageDataUri);
    };
    reader.readAsDataURL(file);
  }
  // const SampleDocument = () => (
  //   <Document>
  //     <Text>Hello World</Text>
  //   </Document>
  // )
  const createDocument = () => {
    const doc = new Document({
      creator: "Clippy",
      title: "Sample Document",
      description: "A brief example of using docx",
      styles: {
          default: {
              listParagraph: {
                  run: {
                      color: "#FF0000",
                  },
              },
          },
          paragraphStyles: [
              {
                  id: "aside",
                  name: "Aside",
                  basedOn: "Normal",
                  next: "Normal",
                  run: {
                      color: "999999",
                      italics: true,
                  },
                  paragraph: {

                      spacing: {
                          line: 276,
                      },
                  },
              },
              {
                  id: "wellSpaced",
                  name: "Well Spaced",
                  basedOn: "Normal",
                  quickFormat: true,
                  paragraph: {
                      spacing: { line: 276, before: 20 * 72 * 0.1, after: 20 * 72 * 0.05 },
                  },
              },
              {
                  id: "strikeUnderline",
                  name: "Strike Underline",
                  basedOn: "Normal",
                  quickFormat: true,
                  run: {
                      strike: true,

                  },
              },
          ],

      },

      sections: [
          {
              children: [
                  new Paragraph({
                      text: "Test heading1, bold and italicized",
                      heading: HeadingLevel.HEADING_1,
                  }),
                  new Paragraph("Some simple content"),
                  new Paragraph({
                      text: "Test heading2 with double red underline",
                      heading: HeadingLevel.HEADING_2,
                  }),
                  new Paragraph({
                      text: "Option1",
                      numbering: {
                          reference: "my-crazy-numbering",
                          level: 0,
                      },
                      style: "aside",
                  }),
                  new Paragraph({
                      text: "Option5 -- override 2 to 5",
                      numbering: {
                          reference: "my-crazy-numbering",
                          level: 0,
                      },
                  }),
                  new Paragraph({
                      text: "Option3",
                      numbering: {
                          reference: "my-crazy-numbering",
                          level: 0,
                      },
                  }),
                  new Paragraph({
                      children: [
                          new TextRun({
                              text: "Some monospaced content",
                              font: {
                                  name: "Monospace",
                              },
                          }),
                      ],
                  }),
                  new Paragraph({
                      text: "An aside, in light gray italics and indented",
                      style: "aside",
                  }),
                  new Paragraph({
                      text: "This is normal, but well-spaced text",
                      style: "wellSpaced",
                  }),
                  new Paragraph({
                      children: [
                          new TextRun({
                              text: "This is a bold run,",
                              bold: true,
                          }),
                          new TextRun(" switching to normal "),
                          new TextRun({
                              text: "and then underlined ",
                              underline: {},
                          }),
                          new TextRun({
                              text: "and then emphasis-mark ",
                              emphasisMark: {},
                          }),
                          new TextRun({
                              text: "and back to normal.",
                          }),
                          new TextRun({
                              text: "This text will be invisible!",
                              vanish: true,
                          }),
                          new TextRun({
                              text: "This text will be VERY invisible! Word processors cannot override this!",
                              specVanish: true,
                          }),
                      ],
                  }),
                  new Paragraph({
                      style: "Strong",
                      children: [
                          new TextRun({
                              text: "Strong Style",
                          }),
                          new TextRun({
                              text: " - Very strong.",
                          }),
                      ],
                  }),
                  new Paragraph({
                      style: "strikeUnderline",
                      children: [
                          new TextRun({
                              text: "Underline and Strike",
                          }),
                      ],
                  }),
                  new Paragraph({
                      children: [
                          new TextRun({
                              text: "Hello World ",
                          }),
                          new TextRun({
                              style: "strikeUnderlineCharacter",
                              text: "Underline and Strike",
                          }),
                          new TextRun({
                              text: " Another Hello World",
                          }),
                          new TextRun({
                              scale: 50,
                              text: " Scaled text",
                          }),
                      ],
                  }),
                  new Paragraph({
                      scale: 200,
                      children: [
                          new TextRun({
                              text: "Scaled paragraph",
                          }),
                      ],
                  }),
              ],
          },
      ],
  });
    return doc;
  };
  return (
    <div className="App">
      <div>
        <p>Choose an Image</p>
        <input
          type="file"
          name=""
          id=""
          onChange={handleImageChange}
          accept="image/*"
        />
      </div>
      <div className="display-flex">
        <img src={imageData} alt="" srcset="" />
        <p>{ocr}</p>
        <button
          onClick={() => {
            const doc = createDocument();

            Packer.toBlob(doc).then((blob) => {
              console.log(blob);
              saveAs(blob, "example.docx");
              console.log("Document created successfully");
            });
          }}
        >
          generate doc
        </button>
      </div>
    </div>
  );
}

export default App;
